INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Five Nights at Freddy''s', 'Recently fired and desperate for work, a troubled young man named Mike agrees to take a position as a night security guard at an abandoned theme restaurant: Freddy Fazbear''s Pizzeria. But he soon discovers that nothing at Freddy''s is what it seems.', '2023-10-25', 'https://image.tmdb.org/t/p/original//A4j8S6moJS2zNtRR8oWF08gRnL5.jpg');
