INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('The Equalizer 3', 'Robert McCall finds himself at home in Southern Italy but he discovers his friends are under the control of local crime bosses. As events turn deadly, McCall knows what he has to do: become his friends'' protector by taking on the mafia.', '2023-08-30', 'https://image.tmdb.org/t/p/original//b0Ej6fnXAP8fK75hlyi2jKqdhHz.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Please Don''t Destroy: The Treasure of Foggy Mountain','When three friends who live together realize that they don''t like their life trajectory, they set off to find a gold treasure that is rumored to be buried in the nearby mountain.', '2023-11-17', 'https://image.tmdb.org/t/p/original//2WSTjQNamyMoy3kFDemd1os8TNz.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('PAW Patrol: The Mighty Movie', 'A magical meteor crash lands in Adventure City and gives the PAW Patrol pups superpowers, transforming them into The Mighty Pups.', '2023-09-21', 'https://image.tmdb.org/t/p/original//aTvePCU7exLepwg5hWySjwxojQK.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Mission: Impossible - Dead Reckoning Part One', 'Ethan Hunt and his IMF team embark on their most dangerous mission yet: To track down a terrifying new weapon that threatens all of humanity before it falls into the wrong hands. With control of the future and the world''s fate at stake and dark forces from Ethan''s past closing in, a deadly race around the globe begins. Confronted by a mysterious, all-powerful enemy, Ethan must consider that nothing can matter more than his mission—not even the lives of those he cares about most.', '2023-07-08', 'https://image.tmdb.org/t/p/original//NNxYkU70HPurnNCSiCjYAmacwm.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Expend4bles', 'Armed with every weapon they can get their hands on and the skills to use them, The Expendables are the world’s last line of defense and the team that gets called when all other options are off the table. But new team members with new styles and tactics are going to give “new blood” a whole new meaning.', '2023-09-15', 'https://image.tmdb.org/t/p/original//iwsMu0ehRPbtaSxqiaUDQB9qMWT.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Gran Turismo', 'The ultimate wish-fulfillment tale of a teenage Gran Turismo player whose gaming skills won him a series of Nissan competitions to become an actual professional racecar driver.', '2023-08-09', 'https://image.tmdb.org/t/p/original//51tqzRtKMMZEYUpSYkrUE7v9ehm.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Desperation Road', 'After 11 years in a Mississippi state prison, Russell Gaines struggles to leave his past sins behind him as he returns home to his stoic father, Mitchell. One fateful night he meets Maben, a young mother with nothing but a stolen gun and a murdered police deputy to her name. Desperate and on the run, Russell and Maben must trust one another to escape their own circumstances, before the truths of their intertwined violent past threatens to destroy them.', '2023-10-19', 'https://image.tmdb.org/t/p/original//tPyj6Gii1HrnzCbJXEF7JdSFkQ8.jpg');
