INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Blue Beetle', 'Recent college grad Jaime Reyes returns home full of aspirations for his future, only to find that home is not quite as he left it. As he searches to find his purpose in the world, fate intervenes when Jaime unexpectedly finds himself in possession of an ancient relic of alien biotechnology: the Scarab.', '2023-08-16', 'https://image.tmdb.org/t/p/original//mXLOHHc1Zeuwsl4xYKjKh2280oL.jpg');
