INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('A Haunting in Venice', 'Celebrated sleuth Hercule Poirot, now retired and living in self-imposed exile in Venice, reluctantly attends a Halloween séance at a decaying, haunted palazzo. When one of the guests is murdered, the detective is thrust into a sinister world of shadows and secrets.', '2023-09-13', 'https://image.tmdb.org/t/p/original//1Xgjl22MkAZQUavvOeBqRehrvqO.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Once Upon a Studio', 'Created for Disney''s 100th anniversary, the short features Mickey Mouse corralling a gallery of legendary Disney characters for a group photo.', '2023-09-24', 'https://image.tmdb.org/t/p/original//hr8ZtwdbtjBquxlTCrczr685K5T.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('LEGO Marvel Avengers: Code Red', 'The Avengers gather to celebrate their latest victory, but their celebration is quickly interrupted by the mysterious disappearance of Black Widow''s father, Red Guardian. As the Avengers investigate, they discover that Red Guardian isn''t the only one missing when they meet a dangerous new foe quite unlike anything they''ve ever encountered before.', '2023-10-26', 'https://image.tmdb.org/t/p/original//rDzig50dj7VpLwJ7SThbamETK1G.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Transformers: Rise of the Beasts', 'When a new threat capable of destroying the entire planet emerges, Optimus Prime and the Autobots must team up with a powerful faction known as the Maximals. With the fate of humanity hanging in the balance, humans Noah and Elena will do whatever it takes to help the Transformers as they engage in the ultimate battle to save Earth.', '2023-06-06', 'https://image.tmdb.org/t/p/original//gPbM0MK8CP8A174rmUwGsADNYKD.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Nowhere', 'A young pregnant woman named Mia escapes from a country at war by hiding in a maritime container aboard a cargo ship. After a violent storm, Mia gives birth to the child while lost at sea, where she must fight to survive.', '2023-09-29', 'https://image.tmdb.org/t/p/original//zwKaPkkLizCg1onpHQq89LWugkS.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Elemental', 'In a city where fire, water, land and air residents live together, a fiery young woman and a go-with-the-flow guy will discover something elemental: how much they have in common.', '2023-06-14', 'https://image.tmdb.org/t/p/original//4Y1WNkd88JXmGfhtWR7dmDAo1T2.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Mortal Kombat Legends: Cage Match', 'In 1980s Hollywood, action star Johnny Cage is looking to become an A-list actor. But when his costar, Jennifer, goes missing from set, Johnny finds himself thrust into a world filled with shadows, danger, and deceit. As he embarks on a bloody journey, Johnny quickly discovers the City of Angels has more than a few devils in its midst.', '2023-10-17', 'https://image.tmdb.org/t/p/original//1eKWqTHp4OgKdx1QX1O9LxKHr1M.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('The Engineer', 'As Israel is rocked by a series of terrorist bombings, a US senator''s daughter is killed in one bloody explosion. Now, ex-Mossad agent Etan must lead an elite, covert team of agents and mercenaries to find the man responsible—the elusive “Engineer.” Can they find and destroy the madman before more innocent lives are lost?', '2023-08-18', 'https://image.tmdb.org/t/p/original//ozkZ8TeIRwzNH2zF5LFS39M3v5U.jpg');
