INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Saw IV', 'Jigsaw and his apprentice Amanda are dead. Now, upon the news of Detective Kerry''s murder, two seasoned FBI profilers, Agent Strahm and Agent Perez, arrive in the terrified community to assist the veteran Detective Hoffman in sifting through Jigsaw''s latest grisly remains and piecing together the puzzle. However, when SWAT Commander Rigg is abducted and thrust into a game, the last officer untouched by Jigsaw has but ninety minutes to overcome a series of demented traps and save an old friend or face the deadly consequences.', '2007-10-25', 'https://image.tmdb.org/t/p/original//owzhppFoegtjAkEhyxQhRtgPl96.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Twilight', 'When Bella Swan moves to a small town in the Pacific Northwest, she falls in love with Edward Cullen, a mysterious classmate who reveals himself to be a 108-year-old vampire. Despite Edward''s repeated cautions, Bella can''t stay away from him, a fatal move that endangers her own life.', '2008-11-20', 'https://image.tmdb.org/t/p/original//3Gkb6jm6962ADUPaCBqzz9CTbn9.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('The Hunger Games: The Ballad of Songbirds & Snakes', '64 years before he becomes the tyrannical president of Panem, Coriolanus Snow sees a chance for a change in fortunes when he mentors Lucy Gray Baird, the female tribute from District 12.', '2023-11-15', 'https://image.tmdb.org/t/p/original//mBaXZ95R2OxueZhvQbcEWy2DqyO.jpg');
INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('In the Fire', 'A couple has to face both the villagers and the local priest, who worry their son is possessed by demonic forces and is the reason for all the village’s woes.  After the death of his wife, Don Marquez brings in Grace Victoria Burnham, an American psychiatrist to find out what is ailing the boy.', '2023-09-14', 'https://image.tmdb.org/t/p/original//rS6GBWRgKkvmcPvtKNuhGkQggjz.jpg');
