INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('Freestyle', 'Trying to check out a recording from his debut album, a street rapper and his friend run into trouble when a major drug deal turns into a total disaster for them.', '2023-09-13', 'https://image.tmdb.org/t/p/original//hNPJzlPlIVgTAjb2Mgqwl1QqydK.jpg');
