INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('SpongeBob SquarePants Presents The Tidal Zone', 'GrandPat travels through alternate dimensions and timelines to get home.', '2023-01-13', 'https://image.tmdb.org/t/p/original//rozwpOPiTEYnXygWGvUPST4lVQa.jpg');
