INSERT INTO movies (title, description, releaseDate, posterImage) VALUES ('The Conjuring 2', 'Lorraine and Ed Warren travel to north London to help a single mother raising four children alone in a house plagued by malicious spirits.', '2016-06-08', 'https://image.tmdb.org/t/p/original//zEqyD0SBt6HL7W9JQoWwtd5Do1T.jpg');
